mapper.select:


SELECT *
FROM (
SELECT
ROWNUM AS RNUM, p.*
FROM (
SELECT *
FROM posts p
WHERE category_id = #{categoryId}
AND is_deleted = 0
ORDER BY updated_dt DESC
) p
)
WHERE RNUM BETWEEN #{StartIndex} AND #{CntPerPage}

mapper.update:
update posts set
view_cnt = (select view_cnt from posts where id = #{id}) + 1
where id = #{id}

mapper.select:
select count(*)
from likes
where post_id=#{postId}
and user_id=#{userId}

select count(*)
from likes
where post_id=#{postId}

mapper.delete:
delete likes
where user_id=#{userId}
and post_id=#{postId}
